import React from 'react';
import Cards from './Cards';
import imagen1 from './../assets/images/imagen1.jpg';
import imagen2 from './../assets/images/imagen2.jpeg';
import elevador from './../assets/images/elevador.jpg';
import imagen8 from './../assets/images/imagen8.jpg';



const HomePage = () => {
  return (
    <div>
      {/* Sección de bienvenida */}
      <div className="relative w-full h-screen flex items-center justify-center">
        <img
          src={imagen8}
          alt="Imagen de bienvenida"
          className="absolute inset-0 w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black opacity-50 z-0"></div>
        <div className="relative z-10 max-w-2xl p-4 text-center">
          <h1 className="text-4xl sm:text-3xl text-2xl font-bold text-white mb-6">
            ¡Bienvenido a Instalaciones XYZ!
          </h1>
          <p className="text-lg sm:text-base text-sm text-white">
            Nos especializamos en instalaciones de elevadores y escaleras eléctricas, garantizando eficiencia y seguridad en cada proyecto.
          </p>
          <button className="mt-6 px-4 py-2 bg-black text-white rounded hover:bg-gray-700 transition duration-300">
            Más Información
          </button>
        </div>
      </div>

    
    </div>
  );
};

export default HomePage;
