import React from 'react';

const Cards = () => {
  const cardData = [
    {
      title: 'Instalación de Elevadores',
      description: 'Ofrecemos servicios de instalación de elevadores con los más altos estándares de calidad.',
    },
    {
      title: 'Mantenimiento Preventivo',
      description: 'Garantizamos el correcto funcionamiento de tus equipos con nuestro mantenimiento preventivo.',
    },
    {
      title: 'Reparación de Escaleras Eléctricas',
      description: 'Reparamos escaleras eléctricas asegurando su eficiencia y seguridad.',
    },
  ];

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      {cardData.map((card, index) => (
        <div key={index} className="bg-white shadow-md rounded-lg p-6">
          <h3 className="text-xl font-bold text-gray-800 mb-4">{card.title}</h3>
          <p className="text-gray-600">{card.description}</p>
        </div>
      ))}
    </div>
  );
};

export default Cards;
