import React from 'react';

// Importa tus imágenes locales
import imgElevador from '../assets/images/elevador.jpg';
import imagen2 from './../assets/images/imagen2.jpeg';
import elevador from './../assets/images/elevador.jpg';
// Datos de las cards
const cardData = [
  {
    title: 'ELEVADORES',
    image: imgElevador,
  },
  {
    title: 'ESCALERAS ELÉCTRICAS',
    image: imagen2,
  },
  {
    title: 'OTROS',
    image: elevador,
  },
];

const Cards = () => {
  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 p-6 place-items-center">
      {cardData.map((item, index) => (
        <div
          key={index}
          className="relative bg-white rounded-2xl shadow-lg overflow-hidden w-72 h-96 transform hover:scale-105 transition-transform duration-300"
        >
          {/* Imagen */}
          <img
            src={item.image}
            alt={item.title}
            className="w-full h-full object-cover"
          />

          {/* Overlay para el título */}
          <div className="absolute bottom-0 w-full bg-black bg-opacity-60 text-white py-2 text-center">
            <h2 className="text-sm font-semibold uppercase">{item.title}</h2>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cards;
