import { useState } from "react";
import Cards from "./Cards";

export default function BuscadorServicios() {
    const [busqueda, setBusqueda] = useState("");
    const [categoria, setCategoria] = useState("todos");

    // Datos de ejemplo para las tarjetas
    const servicios = [
        { id: 1, nombre: "Servicio de Elevador", categoria: "elevadores" },
        { id: 2, nombre: "Mantenimiento de Escaleras", categoria: "escaleras" },
        { id: 3, nombre: "Reparación General", categoria: "otros" },
        { id: 4, nombre: "Instalación de Elevador", categoria: "elevadores" },
    ];

    // Filtrar los resultados según la búsqueda y la categoría
    const resultadosFiltrados = servicios.filter((servicio) => {
        const coincideCategoria =
            categoria === "todos" || servicio.categoria === categoria;
        const coincideBusqueda = servicio.nombre
            .toLowerCase()
            .includes(busqueda.toLowerCase());
        return coincideCategoria && coincideBusqueda;
    });

    return (
        <div className="p-4 max-w-xl mx-auto">
            <h1 className="text-2xl font-bold mb-4">Buscar Servicios</h1>

            <input
                type="text"
                placeholder="Buscar..."
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
                className="w-full p-2 border border-gray-300 rounded mb-4"
            />

            <div className="flex gap-2 mb-4">
                {["todos", "elevadores", "escaleras", "otros"].map((cat) => (
                    <button
                        key={cat}
                        onClick={() => setCategoria(cat)}
                        className={`px-3 py-1 rounded ${
                            categoria === cat
                                ? "bg-black text-white"
                                : "bg-gray-200 hover:bg-gray-300"
                        }`}
                    >
                        {cat.charAt(0).toUpperCase() + cat.slice(1)}
                    </button>
                ))}
            </div>

                {resultadosFiltrados.length > 0 ? (
                    resultadosFiltrados.map((item) => (
                        <li key={item.id} className="p-3 bg-white rounded shadow">
                            {item.nombre} -{" "}
                            <span className="text-sm text-gray-500">{item.categoria}</span>
                        </li>
                    ))
                ) : (
                    <p className="text-gray-500">No se encontraron resultados.</p>
                )}
        
        </div>
    );
}

const CardsSection = () => {
    return (
        <div className="py-16 bg-gray-100">
            <div className="container mx-auto px-4">
                <Cards />
            </div>
        </div>
    );
};
