import React from 'react';
import { useLocation, Routes, Route } from 'react-router-dom';
import Menu from './Menu/menu.jsx';
import Login from './Login/login.jsx';
import Contacto from './Menu/contactanos.jsx';
import Nosotros from './Menu/nosotros.jsx';
import Servicios from './Menu/servicios.jsx';
import Inicio from './Menu/inicio.jsx';
import Carousel from './Menu/carousel.jsx';


const Layout = () => {
  const location = useLocation();  // Aquí puedes usar useLocation porque ya está dentro del Router
  const isHomePage = location.pathname === '/home';  // Detecta si estamos en la página de inicio

  return (
    <div>
      <Menu isHomePage={isHomePage} />  {/* Pasamos la prop al menú */}
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/contacto" element={<Contacto />} />
        <Route path="/nosotros" element={<Nosotros />} />
        <Route path="/servicios" element={<Servicios />} />
        <Route path="/home" element={<Inicio />} />
        <Route path="/carousel" element={<Carousel />} />

      </Routes>
    </div>
  );
};

export default Layout;
