import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Login from './login/Login.jsx';
import Menu from './navbar/Menu.jsx';
import Contacto from './components/Contactanos.jsx';
import Nosotros from './components/Nosotros.jsx';
import Servicios from './components/Servicios.jsx';
import Inicio from './components/Inicio.jsx';


function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

function AppContent() {
  const location = useLocation();

  // Rutas donde NO queremos mostrar el Menu
  const noMenuRoutes = ["/login", "/signup"];

  const hideMenu = noMenuRoutes.includes(location.pathname);

  return (
    <div className="App">
      {!hideMenu && <Menu />}
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/contacto" element={<Contacto />} />
        <Route path="/nosotros" element={<Nosotros />} />
        <Route path="/servicios" element={<Servicios />} />
        <Route path="/home" element={<Inicio />} />
      </Routes>
    </div>
  );
}

export default App;
