import { useState } from "react";

const data = [
  { id: 1, nombre: "Elevador hidráulico", categoria: "elevadores" },
  { id: 2, nombre: "Escalera eléctrica", categoria: "escaleras" },
  { id: 3, nombre: "Mantenimiento general", categoria: "otros" },
  { id: 4, nombre: "Elevador panorámico", categoria: "elevadores" },
  { id: 5, nombre: "Escalera fija", categoria: "escaleras" },
];

export default function BuscadorServicios() {
  const [busqueda, setBusqueda] = useState("");
  const [categoria, setCategoria] = useState("todos");

  const resultadosFiltrados = data.filter((item) => {
    const coincideBusqueda = item.nombre.toLowerCase().includes(busqueda.toLowerCase());
    const coincideCategoria = categoria === "todos" || item.categoria === categoria;
    return coincideBusqueda && coincideCategoria;
  });

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Buscar Servicios</h1>

      <input
        type="text"
        placeholder="Buscar..."
        value={busqueda}
        onChange={(e) => setBusqueda(e.target.value)}
        className="w-full p-2 border border-gray-300 rounded mb-4"
      />

      <div className="flex gap-2 mb-4">
        {["todos", "elevadores", "escaleras", "otros"].map((cat) => (
          <button
            key={cat}
            onClick={() => setCategoria(cat)}
            className={`px-3 py-1 rounded ${
              categoria === cat
                ? "bg-black text-white"
                : "bg-gray-200 hover:bg-gray-300"
            }`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      <ul className="space-y-2">
        {resultadosFiltrados.length > 0 ? (
          resultadosFiltrados.map((item) => (
            <li key={item.id} className="p-3 bg-white rounded shadow">
              {item.nombre} - <span className="text-sm text-gray-500">{item.categoria}</span>
            </li>
          ))
        ) : (
          <p className="text-gray-500">No se encontraron resultados.</p>
        )}
      </ul>
    </div>
  );
}
