import { useState } from "react";
import Cards from "../components/Cards"; // ajusta el path si es necesario
import imgElevador from "../assets/images/elevador.jpg";
import imagen8 from "../assets/images/imagen8.jpg";
import imagen1 from "../assets/images/imagen1.jpg";

const BuscadorServicios = () => {
  const [busqueda, setBusqueda] = useState("");
  const [categoria, setCategoria] = useState("todos");

  // Datos con imágenes, nombre y categoría
  const servicios = [
    {
      id: 1,
      title: "Servicio de Elevador",
      categoria: "elevadores",
      image: imgElevador,
    },
    {
      id: 2,
      title: "Mantenimiento de Escaleras",
      categoria: "escaleras",
      image: imagen8,
    },
    {
      id: 3,
      title: "Reparación General",
      categoria: "otros",
      image: imagen1,
    },
    {
      id: 4,
      title: "Instalación de Elevador",
      categoria: "elevadores",
      image: imgElevador,
    },
  ];

  // Filtrado
  const resultadosFiltrados = servicios.filter((servicio) => {
    const coincideCategoria =
      categoria === "todos" || servicio.categoria === categoria;
    const coincideBusqueda = servicio.title
      .toLowerCase()
      .includes(busqueda.toLowerCase());
    return coincideCategoria && coincideBusqueda;
  });

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Buscar Servicios</h1>

      <input
        type="text"
        placeholder="Buscar..."
        value={busqueda}
        onChange={(e) => setBusqueda(e.target.value)}
        className="w-full p-2 border border-gray-300 rounded mb-4"
      />

      <div className="flex gap-2 mb-6 flex-wrap">
        {["todos", "elevadores", "escaleras", "otros"].map((cat) => (
          <button
            key={cat}
            onClick={() => setCategoria(cat)}
            className={`px-3 py-1 rounded ${
              categoria === cat
                ? "bg-black text-white"
                : "bg-gray-200 hover:bg-gray-300"
            }`}
          >
            {cat.charAt(0).toUpperCase() + cat.slice(1)}
          </button>
        ))}
      </div>

      {/* Aquí van las cards filtradas */}
      <Cards data={resultadosFiltrados} />
    </div>
  );
};

export default BuscadorServicios;
