import { useState } from "react";
import { useNavigate } from "react-router-dom"; // Importa useNavigate
import elevador from "../assets/images/elevador.jpg"; // Asegúrate de que la ruta sea correcta

export default function Signup() {
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate(); // Inicializa useNavigate

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Sign up info:", form);
    // Aquí puedes hacer la petición con Axios u otro método a tu backend
    navigate("/home"); // Redirige al home después de enviar el formulario
  };

  return (
    <div className="min-h-screen flex">
      {/* Imagen lado izquierdo */}
      <div className="w-1/2 hidden md:block">
        <img
          src={elevador}
          alt="Elevador"
          className="h-full w-full object-cover"
        />
      </div>

      {/* Formulario lado derecho */}
      <div className="w-full md:w-1/2 flex items-center justify-center p-8 bg-gray-50">
        <div className="w-full max-w-md bg-white rounded-2xl p-8 shadow-md">
          <h2 className="text-2xl font-bold text-center mb-6">SIGN UP</h2>
          <form onSubmit={handleSubmit} className="space-y-5">
            <div>
              <label className="block text-sm mb-1" htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
            </div>
            <div>
              <label className="block text-sm mb-1" htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                value={form.password}
                onChange={handleChange}
                className="w-full border border-gray-300 rounded-md px-4 py-2 focus:outline-none focus:ring-2 focus:ring-black"
                required
              />
            </div>

            <div className="text-right text-sm">
              <a href="#" className="text-gray-500 hover:underline">Forgot password?</a>
            </div>

            <button
              type="submit"
              className="w-full bg-black text-white py-2 rounded-md shadow-md hover:bg-gray-900 transition"
            >
              Sign up
            </button>

            <p className="mt-4 text-center text-sm text-gray-600">
              Don’t have an account?{" "}
              <a href="#" className="text-black font-semibold hover:underline">Sign up</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
}
