import React from 'react';
import imgElevador from '../assets/images/elevador.jpg';
import imagen8 from './../assets/images/imagen8.jpg';
import imagen1 from './../assets/images/imagen1.jpg';

const cardData = [
  {
    title: 'ELEVADORES',
    image: imgElevador,
    categoria: 'elevadores',
  },
  {
    title: 'ESCALERAS ELÉCTRICAS',
    image: imagen8,
    categoria: 'escaleras',
  },
  {
    title: 'OTROS',
    image: imagen1,
    categoria: 'otros',
  },
];

const Cards = ({ categoriaSeleccionada, busqueda }) => {
  const filtradas = cardData.filter((item) => {
    const coincideCategoria =
      categoriaSeleccionada === 'todos' || item.categoria === categoriaSeleccionada;

    const coincideBusqueda = item.title
      .toLowerCase()
      .includes(busqueda.toLowerCase());

    return coincideCategoria && coincideBusqueda;
  });

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 p-6 place-items-center">
      {filtradas.length > 0 ? (
        filtradas.map((item, index) => (
          <div
            key={index}
            className="relative bg-white rounded-2xl shadow-lg overflow-hidden w-72 h-96 transform hover:scale-105 transition-transform duration-300"
          >
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-full object-cover"
            />
            <div className="absolute bottom-0 w-full bg-black bg-opacity-60 text-white py-2 text-center">
              <h2 className="text-sm font-semibold uppercase">{item.title}</h2>
            </div>
          </div>
        ))
      ) : (
        <p className="text-gray-500 col-span-full">No se encontraron resultados.</p>
      )}
    </div>
  );
};

export default Cards;
