import React from 'react';
import { useLocation, Routes, Route } from 'react-router-dom';
import Menu from '../Menu/menu.jsx';  // Ajusta la ruta según la estructura de tu proyecto
import Login from '../Login/login.jsx';  // Ajusta la ruta
import Contacto from '../Menu/contactanos.jsx';  // Ajusta la ruta
import Nosotros from '../Menu/nosotros.jsx';  // Ajusta la ruta
import Servicios from '../Menu/servicios.jsx';  // Ajusta la ruta
import Inicio from '../Menu/inicio.jsx';  // Ajusta la ruta
import Carousel from '../Menu/carousel.jsx';  // Ajusta la ruta


function App() {
  const location = useLocation();  // Obtener la ruta actual
  const isHomePage = location.pathname === '/home';  // Comprobar si estamos en la página de inicio

  return (
    <div className="App">
      <Menu isHomePage={isHomePage} />  {/* Pasar la prop isHomePage */}
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/contacto" element={<Contacto />} />
        <Route path="/nosotros" element={<Nosotros />} />
        <Route path="/servicios" element={<Servicios />} />
        <Route path="/home" element={<Inicio />} />
        <Route path="/carousel" element={<Carousel />} />
        <Route path="/layout" element={<Layout />} />

      </Routes>
    </div>
  );
}

export default App;
